/* * * @(#)TypeIIResponse.java 0.2 98/11/30 * * Copyright (c) 1998 Ginger Booth.  All Rights Reserved. * * @history: original, simple functional response (no derivative) * Arguments are encounter rate and handling time. * @author Ginger Booth * @version 0.2, 98/11 */package eco.iso;import courseware.util.*;import courseware.plot.*;public class TypeIIResponse extends FnParam {  String defName = "TypeIIResponse";  public TypeIIResponse() {    super();    name = defName;    setArgLabel(0, "Function");    setArgLabel(1, "Kill rate");    setArgLabel(2, "Handling time");  }    public double fOfX(double x, double stepx) {    double encounterRate = numberValue[0];    double handlingTime = numberValue[1];    double denom = (1 + encounterRate * handlingTime * x);    return encounterRate * x / denom;  }  public String toString() {    if (numberValue == null)      return name + "[uninitialized]";    return name + "[encounter rate=" + numberValue[0] + ", handlingTime=" +      numberValue[1] + "]";  }}