/* * * @(#)TypeIResponse.java 0.2 98/11/30 * * Copyright (c) 1998 Ginger Booth.  All Rights Reserved. * * @history: original, simple functional response (no derivative) * Argument is encounter rate. * @author Ginger Booth * @version 0.2, 98/11 */package eco.iso;import courseware.util.*;import courseware.plot.*;public class TypeIResponse extends FnParam {  String defName = "TypeIResponse";  public TypeIResponse() {    super();    name = defName;    setArgLabel(0, "Function");    setArgLabel(1, "Kill rate");  }    public double fOfX(double x, double stepx) {    double encounterRate = numberValue[0];    return encounterRate * x;  }  public String toString() {    if (numberValue == null)      return name + "[uninitialized]";    return name + "[encounter rate=" + numberValue[0] + "]";  }}